record.field:
if not record.grade_level:
    raise Warning("No grade level set for application!")

level = 0
try:
    level = int(record.grade_level.name)
except:
    pass

if level >= 2:
    env["formio.automation"].process(record, "adm_formio.adm_application_formio_automation_teacher_assessment_2")
else:
    env["formio.automation"].process(record, "adm_formio.adm_application_formio_automation_teacher_assessment_pk")
